var obj = {
    get prop() {
        return this.__prop__;
    },
    set prop(user) {
        if (user.reset) {
            this.__prop__ = undefined;
        } else {
            this.__prop__ = {
                id: user.getId(),
                name: user.getGivenName(),
                lastName: user.getFamilyName(),
                profilePicture: user.getImageUrl(),
                email: user.getEmail(),
                tokenGoogle: user.id_token
            };
        }
    },
};

function onSignIn(googleUser) {
    signOut();
    var profile = googleUser.getBasicProfile();
    profile.id_token = googleUser.getAuthResponse().id_token;
    obj.prop = profile;
}

function signOut() {
    obj.prop = {
        reset: true
    };
    var auth2 = gapi.auth2.getAuthInstance();
    auth2.signOut().then(function () {
        console.log('User signed out.');
    });
}